'use strict';

const Macroable = require('macroable');
const path = require('path');

/**
 * App Class
 *
 * @alias App
 * @binding Lesswork/App
 * @group Core
 *
 * @class App
 */
class App extends Macroable {
  constructor() {
    super();
  }

  registerGlobals() {
    global.app = use('App');
    global.Helpers = use('Helpers');
    global.config = use('Config');
    global.response = use('Response');
    global.request = use('Request');
    global.state = use('State');
    global.EXP = use('Exception');
    global.Route = use('Route');

    this.registerLintl();

    use('Event').fire('app:registerGlobals');
  }

  registerLintl() {
    const Lintl = use('Lintl');

    global.numberFormat = Lintl.numberFormat;
    global.dateFormat = Lintl.dateFormat;
    global.__ = Lintl.translate;
    global.lang = global.__;

    this.setLocale(config.get('app.locale'));
  }

  run(callback) {
    if (typeof callback === 'string') {
      callback = Route(callback);
    }

    return require('co')(callback).catch(error => {
      require('../../lib/error')(error);
    });
  }

  environment() {
    if (arguments.length > 0) {
      const patterns = typeof arguments[0] === 'object' ? arguments[0] : arguments;

      for (let i in patterns) {
        if (patterns[i] === process.env.APP_ENV) {
          return true;
        }
      }

      return false;
    }

    return process.env.APP_ENV;
  }

  getLocale() {
    return this.locale;
  }

  isLocale(locale) {
    return this.locale === locale;
  }

  setLocale(locale) {
    this.locale = locale;
  }
}

module.exports = App;

'use strict';

class Base {
  callback() {
    state.callback(null, this.IAMPolicy(...arguments));
  }

  IAMPolicy(principalId, effect, resource, context) {
    return {
      principalId,
      policyDocument: {
        Version: '2012-10-17',
        Statement: [{
          Action: 'execute-api:Invoke',
          Effect: effect,
          Resource: resource,
        }, ],
      },
      context,
    };
  }

  getAuthorizationToken() {
    if (!state.event('authorizationToken')) {
      return false;
    }

    const token = state.event('authorizationToken').split(' ');

    if (token.length === 2) {
      return token[1];
    }

    return token[0];
  }

  deny(user) {
    this.callback(user, 'Deny', state.event('methodArn'), {});
  }

  approve(authString, user) {
    this.callback(authString, 'Allow', state.event('methodArn').split('/').slice(0, 2).join('/') + '/*', {
      user
    });
  }
}

module.exports = Base;

'use strict';

class Basic extends require('./Base') {
  auth(user, pass) {
    const authString = 'Basic ' + new Buffer(user + ':' + pass).toString('base64');

    if (typeof state.event('authorizationToken') == 'undefined' || state.event('authorizationToken') !== authString) {
      return this.deny(user);
    }

    this.approve(user, user);
  }
}

module.exports = Basic;

'use strict';

const jwt = require('jsonwebtoken');

class Jwt extends require('./Base') {
  auth(secret) {
    const authString = this.getAuthorizationToken();

    if (!authString) {
      return this.deny(authString);
    }

    jwt.verify(authString, secret, (error, decoded) => {
      if (error) {
        return this.deny(authString);
      }

      this.approve(authString, JSON.stringify(decoded.user));
    });
  }
}

module.exports = Jwt;

const {
  Command
} = require('lesswork-cmd');

const ejs = require('ejs');
const fs = require('fs');
const path = require('path');
const exec = require('child_process').exec;

class BaseCommand extends Command {
  checkIfExists(file) {
    if (typeof file === 'object') {
      let results = false;
      file.forEach((check) => {
        if (fs.existsSync(check)) {
          results = true;
          return;
        }
      });
      return results;
    }
    return fs.existsSync(file);
  }

  mkdir(dir) {
    try {
      if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir);
      }
    } catch (e) {}
  }

  error(error) {
    require('../../../../lib/error')(error);
  }

  run(command, flags) {
    const args = [];

    if (flags) {
      Object.keys(flags).forEach(flag => {
        if (!flags[flag]) {
          return;
        }
        args.push(`--${flag} ${flags[flag]}`);
      });
    }

    const child = exec(`${command} ${args}`,
      (error, stdout, stderr) => {
        if (stderr) {
          this.error(stderr);
        }

        if (error !== null) {
          this.error(error);
        }
      });

    child.stdout.on('data', function (data) {
      console.log(data);
    });

    return child;
  }

  // TO-DO: accept arrays
  ejsToFile(template, destination, options) {
    ejs.renderFile(path.join(__dirname, `/../../../Generators/templates/${template}.tpl`), options, function (err, data) {
      try {
        fs.writeFile(destination, data, 'utf8');
      } catch (err) {
        if (err) {
          throw err;
        }
      }
    });

    return this;
  }
}

module.exports = BaseCommand;

