   * @param  {String}   [toFile = '']
   *
   * @return {String}
   */
  publicPath(toFile = '') {
    return path.join(this._appRoot, '/public', toFile)
  }

  /**
   * Returns path to the config directory.
   *
   * ## Note
   * This method does not check the existence of
   * file.
   *
   * @method configPath
   *
   * @return {String}
   */
  configPath(toFile = '') {
    return path.join(this._appRoot, '/config', toFile)
  }

  /**
   * Returns path to the resources directory or a
   * specific file to the resources directory.
   *
   * ## Note
   * This method does not check the existence of
   * file.
   *
   * @method resourcesPath
   *
   * @param  {String}   [toFile = '']
   *
   * @return {String}
   */
  resourcesPath(toFile = '') {
    return path.join(this._appRoot, '/resources', toFile)
  }

  /**
   * Returns path to the views directory or a
   * specific file to the views directory.
   *
   * ## Note
   * This method does not check the existence of
   * file.
   *
   * @method viewsPath
   *
   * @param  {String}   [toFile = '']
   *
   * @return {String}
   */
  viewsPath(toFile = '') {
    return path.join(this._appRoot, '/resources/views', toFile)
  }

  /**
   * Returns path to the database directory or a
   * specific file to the database directory.
   *
   * ## Note
   * This method does not check the existence of
   * file.
   *
   * @method databasePath
   *
   * @param  {String}   [toFile = '']
   *
   * @return {String}
   */
  databasePath(toFile = '') {
    return path.join(this._appRoot, '/database', toFile)
  }

  /**
   * Returns path to the migrations directory or a
   * specific file to the migrations directory.
   *
   * ## Note
   * This method does not check the existence of
   * file.
   *
   * @method migrationsPath
   *
   * @param  {String}   [toFile = '']
   *
   * @return {String}
   */
  migrationsPath(toFile = '') {
    return path.join(this._appRoot, '/database/migrations', toFile)
  }

  /**
   * Returns path to the seeds directory or a
   * specific file to the seeds directory.
   *
   * ## Note
   * This method does not check the existence of
   * file.
   *
   * @method seedsPath
   *
   * @param  {String}   [toFile = '']
   *
   * @return {String}
   */
  seedsPath(toFile = '') {
    return path.join(this._appRoot, '/database/seeds', toFile)
  }

  /**
   * Returns path to the tmp directory or a
   * specific file to the tmp directory.
   *
   * ## Note
   * This method does not check the existence of
   * file.
   *
   * @method tmpPath
   *
   * @param  {String}   [toFile = '']
   *
   * @return {String}
   */
  tmpPath(toFile = '') {
    return path.join(this._appRoot, '/tmp', toFile)
  }

  /**
   * Promisify callback style functions
   *
   * @method promisify
   *
   * @param  {Function} fn
   * @param  {Object}   options
   *
   * @return {Promise}
   */
  promisify(fn, options) {
    return pify(fn, options)
  }

  /**
   * Tells whether the process has been started by
   * ace command.
   *
   * @method isAceCommand
   *
   * @return {Boolean}
   */
  isAceCommand() {
    const processFile = process.mainModule.filename
    if (processFile.endsWith('ace')) {
      return true
    }

    /**
     * When command is executed via `adonis cli`, then ace is a children
     * of the process mainModule
     */
    return !!process.mainModule.children.find((child) => child.filename.endsWith('ace'))
  }
}

module.exports = Helpers

'use strict';

const Macroable = require('macroable');
const Intl = require('intl');
const IntlMessageFormat = require('intl-messageformat');
const path = require('path');
const _ = require('lodash');

/**
 * Lintl Class
 *
 * @alias Lintl
 * @binding Lesswork/Lintl
 * @group Core
 *
 * @class Lintl
 */
class Lintl extends Macroable {
  constructor(fallback) {
    super();

    this.fallback = fallback;

    this.translate = this._translate.bind(this);
    this.numberFormat = this._numberFormat.bind(this);
    this.dateFormat = this._dateFormat.bind(this);
  }

  _dateFormat(date, locale, fallback = true) {
    locale = locale || use('App').getLocale();

    if (fallback) {
      fallback = this.fallback;
    }

    if (!locale) {
      return date;
    }

    return new Intl.DateTimeFormat(_.without([locale, fallback], null)).format(date);
  }

  _numberFormat(...args) {
    let number = args[0];
    let locale = use('App').getLocale();
    let fallback = true;
    let format = false;

    args.slice(1).forEach(function (_arg) {
      if (typeof _arg === 'string') {
        locale = _arg;
      }

      if (typeof _arg === 'object') {
        format = _arg;
      }

      if (typeof _arg === 'boolean') {
        fallback = _arg;
      }
    });

    if (fallback) {
      fallback = this.fallback;
    }

    if (!locale) {
      return number;
    }

    return new Intl.NumberFormat(_.without([locale, fallback], null), format).format(number);
  }

  _translate(...args) {
    let hash = args[0];
    let locale = null;
    let fallback = true;
    let format = null;

    args.slice(1).forEach(function (_arg) {
      if (typeof _arg === 'string') {
        locale = _arg;
      }

      if (typeof _arg === 'object') {
        format = _arg;
      }

      if (typeof _arg === 'boolean') {
        fallback = _arg;
      }
    });

    const locales = [locale || use('App').getLocale()];

    if (!locales.length) {
      return hash;
    }

    const _hash = hash;
    hash = hash.split('.');

    const file = hash[0];
    if (fallback) {
      locales.push(this.fallback);
    }

    let line = null;

    for (let locale of locales) {
      const config = Helpers.requireIfExists(path.join(Helpers.resourcesPath(path.join('lang', locale)), `${file}.js`));

      if (!config) {
        continue;
      }

      try {
        line = new IntlMessageFormat(hash.slice(1).reduce((o, i) => o[i], config), locale).format(format);

        break;
      } catch (e) {
        continue;
      }
    }

    if (line) {
      return line;
    }

    return _hash;
  }
}

module.exports = Lintl;

